            let pretty_body = Array.from(deps.Strophe.xmlescape(body));
            mutable_refs && mutable_refs.forEach(function (ref) {
                for (let idx = ref.start; idx < ref.end; idx++)
                    pretty_body[idx] = "";
            }.bind(this));
            return deps.Strophe.xmlunescape(pretty_body.join("").trim());
